import React, { useState, useEffect } from "react";
import "../stylesheets/admin.css";
import axios from "axios";
import PopUpForm from "../components/admin/PopUpForm";

const Admin = () => {
  //init variables
  const [accounts, setAccounts] = useState([]);
  const [visibility, setVisibility] = useState(false);

  const [id, setID] = useState();
  const [username, setUsername] = useState();
  const [nickname, setNickname] = useState();

  const [input, setInput] = useState();
  const [option, setOption] = useState("");

  const popupCloseHandler = (e) => {
    setVisibility(e);
  };

  //get the data for popup form
  const dataForForm = (id, username, nickname) => {
    setID(id);
    setUsername(username);
    setNickname(nickname);
  };

  //fetch the accounts data
  const fetchAccounts = async () => {
    const res = await axios
      .get("/admindata/accounts")
      .then((res) => {
        setAccounts(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };


  //change data in database
  const handleChange= async (e) => {

    const userObject = {
      content: {input}
    };

    console.log(userObject);
    await axios
      .post("/admindata/"+{option}, userObject)
      .then((res) => {
        console.log(res.data);

        if (res.data.login) {
          setUser("");
          setPwd("");
          //setSuccess(true);

        } else {
          setErrMsg(res.data.status);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    fetchAccounts();
  }, []);

  //HTML render out
  return (
    <div className="admin">
      <PopUpForm
        onClose={popupCloseHandler}
        show={visibility}
        title={"User ID - " + id}
      >
        <input
          type="text"
          name="content"
          placeholder="Content"
          autoComplete="off"
          onChange={(e) => setUser(e.target.input)}
          value={input}
          required
          maxLength={20}
        />

        <button type="button" className="option"  onClick={(e) => {
                      setOption("/modify/nickname")
                    }}>
          Change Nickname
        </button>
        <button type="button" className="option">
          Change Username
        </button>
        <button type="button" className="option">
          Change Password
        </button>
        <button type="button" className="delete">
          Delete User
        </button>

      </PopUpForm>
      <div>
        <h1>Manage Splittr Accounts</h1>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nick Name</th>
              <th>Username</th>
              <th>Operations</th>
            </tr>
          </thead>
          <tbody>
            {accounts.map((item) => (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.nickname}</td>
                <td>{item.username}</td>
                <td>
                  <button
                    type="button"
                    onClick={(e) => {
                      dataForForm(item.id, item.username, item.nickname);
                      setVisibility(!visibility);
                    }}
                  >
                    Modify
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};
export default Admin;
