import React, { useState, useEffect } from "react";
import "../stylesheets/newform.css";
import axios from "axios";

const Admin = () => {
  //init variables
  const [accounts, setAccounts] = useState([]);

  //fetch the accounts data
  const fetchAccounts = async () => {
    const res = await axios
      .get("/admindata/accounts")
      .then((res) => {
        setAccounts(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  //HTML render out
  return (
    <div className="Admin Table">
      <h1>Admin Splittr Accounts</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nick Name</th>
            <th>Username</th>
          </tr>
        </thead>
        <tbody>
          {accounts.map((item) => (
            <tr key={item.id}>
              <td>
                {item.id}
              </td>
              <td>
                {item.nickname}
              </td>
              <td>
                {item.username}
              </td>
              <td>
                {item.username}
              </td>
              <td>
               {item.username}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
export default Admin;
